import { useState } from "react";

interface DataRow {
  name: string;
  device: string;
  path: string;
  status: string;
}

const sampleData: DataRow[] = [
  { name: "smss.exe", device: "Stark", path: "\\Device\\HarddiskVolume2\\Windows\\System32\\smss.exe", status: "scheduled" },
  { name: "netsh.exe", device: "Targaryen", path: "\\Device\\HarddiskVolume2\\Windows\\System32\\netsh.exe", status: "available" },
  { name: "uxtheme.dll", device: "Lanniester", path: "\\Device\\HarddiskVolume1\\Windows\\System32\\uxtheme.dll", status: "available" },
  { name: "cryptbase.dll", device: "Martell", path: "\\Device\\HarddiskVolume1\\Windows\\System32\\cryptbase.dll", status: "scheduled" },
  { name: "7za.exe", device: "Baratheon", path: "\\Device\\HarddiskVolume1\\temp\\7za.exe", status: "scheduled" }
];

export default function DataGrid() {
  const [selectedRows, setSelectedRows] = useState<string[]>([]);

  const toggleRow = (name: string) => {
    setSelectedRows((prev) =>
      prev.includes(name) ? prev.filter((row) => row !== name) : [...prev, name]
    );
  };

  const toggleAll = () => {
    if (selectedRows.length === sampleData.length) {
      setSelectedRows([]);
    } else {
      setSelectedRows(sampleData.map((row) => row.name));
    }
  };

  const isAllSelected = selectedRows.length === sampleData.length;
  const isSomeSelected = selectedRows.length > 0 && !isAllSelected;

  const availableSelected = selectedRows.every((name) =>
    sampleData.find((row) => row.name === name)?.status === "available"
  );

  return (
    <div>
      <h2>Datagrid</h2>
      <label>
        <input
          type="checkbox"
          onChange={toggleAll}
          checked={isAllSelected}
          ref={(el) => el && (el.indeterminate = isSomeSelected)}
        />
        {selectedRows.length > 0 ? `${selectedRows.length} Selected` : "None Selected"}
      </label>
      <table border={1} style={{ width: "100%", marginTop: "10px" }}>
        <thead>
          <tr>
            <th>Checkbox</th>
            <th>Name</th>
            <th>Device</th>
            <th>Path</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {sampleData.map((row) => (
            <tr key={row.name}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedRows.includes(row.name)}
                  onChange={() => toggleRow(row.name)}
                />
              </td>
              <td>{row.name}</td>
              <td>{row.device}</td>
              <td>{row.path}</td>
              <td>
                {row.status === "available" && <span style={{ color: "green", marginRight: "5px" }}>â¬¤</span>}
                {row.status}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button
        onClick={() => alert(JSON.stringify(selectedRows, null, 2))}
        disabled={!availableSelected || selectedRows.length === 0}
        style={{ marginTop: "10px" }}
      >
        Download Selected
      </button>
    </div>
  );
}
