import { useState } from "react";

type Row = {
  id: number;
  name: string;
  device: string;
  path: string;
  status: "Available" | "Unavailable";
};

const sampleData: Row[] = [
  {
    id: 1,
    name: "File1",
    device: "Laptop",
    path: "/files/file1",
    status: "Available",
  },
  {
    id: 2,
    name: "File2",
    device: "Tablet",
    path: "/files/file2",
    status: "Unavailable",
  },
  {
    id: 3,
    name: "File3",
    device: "Phone",
    path: "/files/file3",
    status: "Available",
  },
];

export default function Datagrid() {
  const [selectedRows, setSelectedRows] = useState<number[]>([]);
  const allAvailable = selectedRows.every(
    (id) => sampleData.find((row) => row.id === id)?.status === "Available"
  );

  const handleSelectRow = (id: number) => {
    setSelectedRows((prev) =>
      prev.includes(id) ? prev.filter((rowId) => rowId !== id) : [...prev, id]
    );
  };

  const handleSelectAll = () => {
    if (selectedRows.length === sampleData.length) {
      setSelectedRows([]);
    } else {
      setSelectedRows(sampleData.map((row) => row.id));
    }
  };

  const isChecked = selectedRows.length === sampleData.length;
  const isIndeterminate =
    selectedRows.length > 0 && selectedRows.length < sampleData.length;

  const handleDownload = () => {
    alert(
      "Selected Rows: " +
        JSON.stringify(
          sampleData.filter((row) => selectedRows.includes(row.id)),
          null,
          2
        )
    );
  };

  return (
    <div>
      <h2>Datagrid</h2>
      <label>
        <input
          type="checkbox"
          checked={isChecked}
          ref={(el) => el && (el.indeterminate = isIndeterminate)}
          onChange={handleSelectAll}
        />
        {selectedRows.length
          ? `${selectedRows.length} Selected`
          : "None Selected"}
      </label>
      <table
         border={1}
        cellPadding="5"
        style={{ width: "100%", marginTop: "10px" }}
      >
        <thead>
          <tr>
            <th></th>
            <th>Name</th>
            <th>Device</th>
            <th>Path</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {sampleData.map((row) => (
            <tr key={row.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedRows.includes(row.id)}
                  onChange={() => handleSelectRow(row.id)}
                />
              </td>
              <td>{row.name}</td>
              <td>{row.device}</td>
              <td>{row.path}</td>
              <td>
                {row.status === "Available" && (
                  <span style={{ color: "green", marginRight: "5px" }}>‚óè</span>
                )}
                {row.status}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button
        onClick={handleDownload}
        disabled={!selectedRows.length || !allAvailable}
        style={{ marginTop: "10px" }}
      >
        Download Selected
      </button>
    </div>
  );
}
