import React, { useState } from "react";

interface DataItem {
  name: string;
  device: string;
  path: string;
  status: string;
}

const sampleData: DataItem[] = [
  { name: "smss.exe", device: "Stark", path: "\\Device\\HarddiskVolume2\\Windows\\System32\\smss.exe", status: "scheduled" },
  { name: "netsh.exe", device: "Targaryen", path: "\\Device\\HarddiskVolume2\\Windows\\System32\\netsh.exe", status: "available" },
  { name: "uxtheme.dll", device: "Lanniester", path: "\\Device\\HarddiskVolume1\\Windows\\System32\\uxtheme.dll", status: "available" },
  { name: "cryptbase.dll", device: "Martell", path: "\\Device\\HarddiskVolume1\\Windows\\System32\\cryptbase.dll", status: "scheduled" },
  { name: "7za.exe", device: "Baratheon", path: "\\Device\\HarddiskVolume1\\temp\\7za.exe", status: "scheduled" }
];

const DataGrid: React.FC = () => {
  const [selectedRows, setSelectedRows] = useState<string[]>([]);

  const toggleRowSelection = (name: string) => {
    setSelectedRows(prev =>
      prev.includes(name) ? prev.filter(item => item !== name) : [...prev, name]
    );
  };

  const toggleSelectAll = () => {
    if (selectedRows.length === sampleData.length) {
      setSelectedRows([]);
    } else {
      setSelectedRows(sampleData.map(item => item.name));
    }
  };

  const isIndeterminate = selectedRows.length > 0 && selectedRows.length < sampleData.length;
  const canDownload = selectedRows.every(name => sampleData.find(item => item.name === name)?.status === "available");

  const handleDownload = () => {
    alert(JSON.stringify(sampleData.filter(item => selectedRows.includes(item.name)), null, 2));
  };
console.log("canDownload---",canDownload)
  return (
    <div>
      <h2>Datagrid</h2>
      <label>
        <input
          type="checkbox"
          checked={selectedRows.length === sampleData.length}
          onChange={toggleSelectAll}
          ref={el => el && (el.indeterminate = isIndeterminate)}
        />
        {selectedRows.length ? `${selectedRows.length} Selected` : "None Selected"}
      </label>
      <table border={1} cellPadding={5} style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th>Checkbox</th>
            <th>Name</th>
            <th>Device</th>
            <th>Path</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {sampleData.map(item => (
            <tr key={item.name}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedRows.includes(item.name)}
                  onChange={() => toggleRowSelection(item.name)}
                />
              </td>
              <td>{item.name}</td>
              <td>{item.device}</td>
              <td>{item.path}</td>
              <td>
                {item.status === "available" && <span style={{ color: "green" }}>‚óè</span>} {item.status}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleDownload} disabled={ selectedRows.length < sampleData.length}>
        Download Selected
      </button>
    </div>
  );
};

export default DataGrid;
